use myDB;
drop table if exists tbl_iolist;
CREATE TABLE if not EXISTS TBL_IOLIST (
	IO_SEQ BIGINT PRIMARY KEY,
	IO_DATE VARCHAR(10),
	IO_INOUT VARCHAR(2),
	IO_QTY INT,
	IO_PRICE INT,
	IO_AMT INT,
	IO_PCODE char(5),
	IO_BCODE char(4)
);

select count(*) from tbl_iolist;

select io_bcode from tbl_iolist;


-- decode()
select io_bcode,
	sum(case when io_inout = '매입' then io_amt else 0 end) as '매입합계',
	sum(case when io_inout = '매출' then io_amt else 0 end) as '매출합계'
from tbl_iolist
group by io_bcode;

select
	sum(case when io_inout = '매입' then io_amt else 0 end) as '매입합계',
	sum(case when io_inout = '매출' then io_amt else 0 end) as '매출합계'
from tbl_iolist;

select
	sum(case when io_inout = '매입' then io_amt else 0 end) as '매입합계',
	sum(case when io_inout = '매출' then io_amt else 0 end) as '매출합계',
    
    sum(case when io_inout = '매출' then io_amt else 0 end) -
    sum(case when io_inout = '매입' then io_amt else 0 end) as 이익금
	
from tbl_iolist;

